        }

        if (MySettings.UseGiftoftheNaaruAtPercentage < HealHP)
        {
            HealHP = MySettings.UseGiftoftheNaaruAtPercentage;
        }

        if (MySettings.UseDeathPactAtPercentage < HealHP)
        {
