            return;
        }
        if (Typhoon.KnownSpell && Typhoon.IsSpellUsable && ObjectManager.GetNumberAttackPlayer() > 2
            && ObjectManager.Target.GetDistance <= 40f && ObjectManager.Me.HealthPercent < 70
