            BloodFury.Launch();
            return;
        }
        if (Lifeblood.IsSpellUsable && Lifeblood.KnownSpell && ObjectManager.Target.GetDistance < 30
            && MySettings.UseLifeblood)
        {
            Lifeblood.Launch();
            return;
        }
        if (_engineeringTimer.IsReady && Engineering.KnownSpell && ObjectManager.Target.GetDistance < 30
