        DefenseCycle();
        Heal();

        if (HeroicThrow.KnownSpell && HeroicThrow.IsSpellUsable && HeroicThrow.IsHostileDistanceGood
            && MySettings.UseHeroicThrow && !ObjectManager.Target.InCombat)
