        }
        if (ObjectManager.GetNumberAttackPlayer() > 3 && MySettings.UseMultiShot && MultiShot.KnownSpell && MultiShot.IsHostileDistanceGood
            && MultiShot.IsSpellUsable)
        {
            MultiShot.Launch();
            return;
