            return;
        }
        if (ArcaneShot.KnownSpell && ArcaneShot.IsSpellUsable && ArcaneShot.IsHostileDistanceGood && MySettings.UseArcaneShot && ObjectManager.Me.FocusPercentage > 64)
        {
            ArcaneShot.Launch();
